document.addEventListener('DOMContentLoaded',function(){if(!localStorage.getItem('cookieConsent')){document.getElementById('cookie-banner').style.display='block';}document.querySelector('.accept').addEventListener('click',function(){localStorage.setItem('cookieConsent','accepted');document.getElementById('cookie-banner').style.display='none';});document.querySelector('.close').addEventListener('click',function(){localStorage.setItem('cookieConsent','closed');document.getElementById('cookie-banner').style.display='none';});});